script:
frappe.ui.form.on('Employee', {
    status_pajak: function(frm) {
        if (frm.doc.status_pajak) {
            // Extract number from status pajak (e.g., K2 -> 2)
            const match = frm.doc.status_pajak.match(/K(\d)/);
            if (match) {
                frm.set_value('jumlah_tanggungan', parseInt(match[1]));
            } else {
                frm.set_value('jumlah_tanggungan', 0);
            }
            frm.refresh_field('jumlah_tanggungan');
        }
    },

    jabatan: function(frm) {
        if (frm.doc.jabatan && frm.doc.golongan) {
            // Validate golongan level against jabatan's max_golongan
            frappe.call({
                method: 'payroll_indonesia.payroll_indonesia.validations.validate_employee_golongan',
                args: {
                    jabatan: frm.doc.jabatan,
                    golongan: frm.doc.golongan
                },
                callback: function(r) {
                    if (r.exc) {
                        frappe.validated = false;
                    }
                }
            });
        }
    },

    golongan: function(frm) {
        if (frm.doc.jabatan && frm.doc.golongan) {
            // Validate golongan level against jabatan's max_golongan
            frappe.call({
                method: 'payroll_indonesia.payroll_indonesia.validations.validate_employee_golongan',
                args: {
                    jabatan: frm.doc.jabatan,
                    golongan: frm.doc.golongan
                },
                callback: function(r) {
                    if (r.exc) {
                        frappe.validated = false;
                    }
                }
            });
        }
    }
});
